<?phpinclude "c:/vendor/autoload.php";use EasyWeChat\Factory;$options = [    'app_id'    => 'wx6db36cbd7e005897',    'secret'    => '934824280622d1dd24a7779df95d14ff',    'token'     => '123456789',    'aes_key' => '',                    // EncodingAESKey，兼容与安全模式下请一定要填写！！！    'log' => [        'level' => 'debug',        'file'  =>  __DIR__.'/wechat.log',    ],];$app = Factory::officialAccount($options);$server = $app->server;$user = $app->user;$server->push(function($message) use ($user, $app) {    switch ($message['MsgType']) {        case 'event':            if ($message['Event'] == 'CLICK') {                if($message['EventKey']=='V1001_CUSTOMER_GUIDE'){                    return '请仔细阅读操作指导！';                }                asyn_sendevent($message['FromUserName'], $message['Event'], $message['EventKey']);            }            if($message['Event'] == 'subscribe') {                $fromUser = $user->get($message['FromUserName']);                return "亲爱的 {$fromUser['nickname']}, 欢迎光临颜晴恋人馆！";            }            break;        case 'text': //           $fromUser = $user->get($message['FromUserName']);//            $app->customer_service->message("From:{$fromUser['nickname']}:\n{$message['Content']}")->to('oA9Ks0uzoUK8RL_k9cfH2J-9xPE4')->send();            asyn_sendmsg($message['FromUserName'],$message['Content']);     //       return '消息已转发，请等待！';            break;        case 'image':            return '收到图片消息';            break;        case 'voice':            return '收到语音消息';            break;        case 'video':            return '收到视频消息';            break;        case 'location':            return '收到坐标消息';            break;        case 'link':            return '收到链接消息';            break;        // ... 其它消息        default:            return '收到其它消息';            break;    }});$server->serve()->send();function asyn_sendmsg($openid, $content) {    $fp=fsockopen('localhost',80,$errno,$errstr,5);    if(!$fp){        echo "$errstr ($errno)<br />\n";    }    fputs($fp,"GET /logic/handleMsg.php?openid=$openid&content=$content\r\n"); #请求的资源 URL 一定要写对    fclose($fp);}function asyn_sendevent($openid, $event, $eventkey) {    $fp=fsockopen('localhost',80,$errno,$errstr,5);    if(!$fp){        echo "$errstr ($errno)<br />\n";    }    fputs($fp,"GET /logic/handleEvent.php?openid=$openid&event=$event&key=$eventkey\r\n"); #请求的资源 URL 一定要写对    fclose($fp);}?>