<?phpasyn_sendevent('oA9Ks0uzoUK8RL_k9cfH2J-9xPE4', 'CLICK', 'V1001_CUSTOMER_STATE');function asyn_sendevent($openid, $event, $eventkey) {    $fp=fsockopen('localhost',80,$errno,$errstr,5);    if(!$fp){        echo "$errstr ($errno)<br />\n";    }    fputs($fp,"GET handleEvent.php?openid=$openid&event=$event&key=$eventkey\r\n"); #请求的资源 URL 一定要写对    fclose($fp);	echo "end";}?>